/**********************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
Tic-Tac-Toe (beta) game by Marin Mirasol                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
**********************************************************************/
#include <iostream> //for cin, cout, and the string class                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
#include <iomanip> //for cin.get to have the user press enter                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
using namespace std;

//global constants for the 2D array sizes                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
const int DIM = 3;
const int PLAYERS = 2;

//program function prototypes                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
void intro( string pNames[ ] );
void initializeTable( char gTable[ ][ DIM ] );
void showTable( const char gTable[ ][ DIM ] );
void playGame( char gTable[ ][ DIM ] , const string pNames[ ] , const int pTurn );
bool checkChoice( const char gTable[ ][ DIM ] , const int choice );
bool checkWinner( const char gTable[ ][ DIM ] , const string pNames[ ] , const int pTurn );
bool checkTie( const char gTable[ ][ DIM ] );

//the main function, which executes the order of operations during the game                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
int main()
{
  char gameTable[ DIM ][ DIM ]; //array that holds the tic-tac-toe game table                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
  string playerNames[ PLAYERS ];  //array that holds the player names                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 

  bool gameOver = false; //inidicates whether the game keeps going                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
  int playerTurn = 0; //indicates the player's turn that plays                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
  int round = 0; //shows which round the that is in progress                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          

  //show the intro and get the player names                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
  intro( playerNames );
  //initialize the table                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
  initializeTable( gameTable );

  do //keep playing until a player wins                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
    {
      round++; //increment the round each time a player plays the game                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                

      //show which the round and which player is playing                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
      cout << "ROUND #" << round << " ( " << playerNames[ playerTurn ] << "'s turn ):" << endl << endl;

      //show the table to the current player                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
      showTable( gameTable );
      cout << endl;

      //have the current player play the game                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
      playGame( gameTable , playerNames , playerTurn );
      //check if a player wins by checking the game table contents                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
      gameOver = checkWinner( gameTable , playerNames , playerTurn );

      //if the game is over, show the table and congratulate the winner                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
      if( gameOver )
        {
          //show the table on the next line to ogranize the outputs                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
          cout << "\nTIC-TAC-TOE, GAME OVER!!!" << endl << endl;
          //show the table to show that the player has won                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
          showTable( gameTable );

          //congratulate the winner that won the game                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
          cout << endl << playerNames[ playerTurn ] << " wins!" << endl;
          cout << "\n-----------------------------------------" << endl;
        }

      //check if there is a tie and let the user's know if there's a tie                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
      if( gameOver == false && checkTie( gameTable ))
        {
          //since there is a tie, the game cannot continues                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
          gameOver = true;

          //show the table on the next line to organize the outputs                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
          cout << "\nTIE, GAME OVER!!!" << endl << endl;
          //show the table to show that no player has won                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
          showTable( gameTable );

          //let the players know that no one won                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
          cout << endl << "Sorry " << playerNames[0] << " and " <<playerNames[1] << ", no one wins!" << endl;
          cout << "\n-----------------------------------------" << endl;
        }

      //change the player turn based on who currently is playing                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
      if( playerTurn == 0 )
        playerTurn = 1;
      else //playerTurn == 1, switch to other player                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
        playerTurn = 0;

    }while( gameOver == false ); //keep playing while the game is not over                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            

  return 0; //end the program once the game is over                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
}

/*******************************************************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
This function will prompt the user's to press enter and then enter their names.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
pNames: the array that holds the player names.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
*******************************************************************************************************/
void intro( string pNames[ ] )
{
  char ch; //needed to allow the user's to press the enter key to proceed                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             

  //welcome the user's and prompt them to press enter to continue                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
  cout << "Welcome to tic-tac-toe, press ENTER to continue.";
  cin.get(ch);

  //prompt the user's to enter their names                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
  cout << endl << "Enter a name for player 1: ";
  cin >> pNames[0];
  cout << "Enter a name for player 2: ";
  cin >> pNames[1];

  //show which marks belong to which player                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
  cout << "-----------------------------------------" << endl;
  cout << pNames[0] << " = X" << endl << pNames[1] << " = O" << endl;
  cout << "-----------------------------------------" << endl;
}

/*******************************************************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
This function will initialize the game table with empty space slots.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
gTable: the 2D array that contains the tic-tac-toe game table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
*******************************************************************************************************/
void initializeTable( char gTable[ ][ DIM ] )
{
  //start from the first row to the last                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
  for( int r = 0 ; r < DIM ; r++ )
    {
      //in the current row, initialize each collumn slot with a space                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
      for( int c = 0 ; c < DIM ; c++ )
        gTable[ r ][ c ] = ' ';
    }
}

/*******************************************************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
This function will show the contents of the game table to the user's to see the game progress.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
gTable: the 2D array that contains the tic-tac-toe game table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
*******************************************************************************************************/
void showTable( const char gTable[ ][ DIM ] )
{
  int count = 0; //used for organizing the vertical output lines between each game table slot                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         

  //start printing from row to row ( horizontally )                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
  for( int r = 0 ; r < DIM ; r++ )
    {
      //in each row print the contents of each column of the row                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
      for( int c = 0 ; c < DIM ; c++ )
        {
          count++; //increment the first counter for column outputs                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   

          //show the contents of the current slot                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
          cout << " " << gTable[ r ][ c ] << " ";

          //only show lines between the three collumns                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
          if( count < 3 )
            cout << "|";
        }

      count = 0; //reset the first counter since there are more rows to print                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
      cout << endl;

      //only show lines between the three rows                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
      if( r + 1 < 3 )
        cout << "-----------" << endl;
    }
}

/*******************************************************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
This function will have the current player play the game by choosing a spot to mark on the table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
gTable: the 2D array that contains the tic-tac-toe game table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
pNames: the array that holds the player names.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
pTurn: the current player turn to play the game.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
*******************************************************************************************************/
void playGame( char gTable[ ][ DIM ] , const string pNames[ ] , const int pTurn )
{
  int choice; //the choice the user chooses to mark the tic-tac-toe game table                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
  char mark; //the current player's mark to mark the tic-tac-toe game table                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
  bool invalid; //indicates whether a choice is invalid or not                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        

  //show all the different spots the current player can mark                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
  cout << " 1 | 2 | 3 " << endl;
  cout << "-----------" << endl;
  cout << " 4 | 5 | 6 " << endl;
  cout << "-----------" << endl;
  cout << " 7 | 8 | 9 " << endl << endl;

  //prompt the current player to mark a spot                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
  cout << pNames[ pTurn ] << " choose a spot to mark: ";
  cin >> choice;

  //check if that spot is valid by checking if it is empty or not                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
  invalid = checkChoice( gTable , choice );

  //while the spot is invalid, keep prompting the player to choose an empty spot                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
  while( invalid )
    {
      //let the player know the spot is invalid                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
      cout << "\tSorry " << pNames[ pTurn ] << " that spot is invalid or was already taken." << endl;
      cout << "\tOnly enter a valid or open spot to mark: ";
      cin >> choice;

      //check the new spot chosen by the player                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
      invalid = checkChoice( gTable , choice );
    }

  //make the mark based on the current player playing                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
  if( pTurn == 0 )
    mark = 'X';
  else //otherwise, pTurn == 1, so the second player is playing                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
    mark = 'O';

  //if the user chooses 1-3, the spots must all be on the first row                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
  if( choice >= 1 && choice <= 3 )
    gTable[ 0 ][ choice - 1 ] = mark;
  //if the user chooses 4-6, the spots must all be on the second row                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
  else if( choice >= 4 && choice <= 6 )
    gTable[ 1 ][ choice - 4 ] = mark;
  //if the user chooses 7-9, the spots must all be on the third row                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
  else if( choice >= 7 && choice <= 9 )
    gTable[ 2 ][ choice - 7 ] = mark;

  //separate the input with a line                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
  cout << "-----------------------------------------" << endl;
}

/*******************************************************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
This function will check if the choice is valid by checking if the spot is empty or was already marked.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
gTable: the 2D array that contains the tic-tac-toe game table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
choice: the choice the user chooses to mark the tic-tac-toe game table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
*******************************************************************************************************/
bool checkChoice( const char gTable[ ][ DIM ] , const int choice )
{
  char space = ' '; //empty space which indicates if a choice is valid or not                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         

  //if the user chooses 1-3, the spot from the first row must be checked                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
  if( choice >= 1 && choice <= 3 )
    return ( gTable[ 0 ][ choice - 1 ] != space )? true:false;
  //if the user chooses 4-6, the spot from the second row must be checked                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
  else if( choice >= 4 && choice <= 6 )
    return ( gTable[ 1 ][ choice - 4 ] != space )? true:false;
  //if the user chooses 7-9, the spot from the third row must be checked                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
  else if( choice >= 7 && choice <= 9 )
    return ( gTable[ 2 ][ choice - 7 ] != space )? true:false;
  else //otherwise, the user entered an invalid choice that is out of bounds                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
    return true;
}

/*******************************************************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
This function will check if a player has won by checking all of the different winning possibilities.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
gTable: the 2D array that contains the tic-tac-toe game table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
gOver: inidicates whether the game keeps going or not.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
pNames: the array that holds the player names.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
pTurn: the current player turn to play the game.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
*******************************************************************************************************/
bool checkWinner( const char gTable[ ][ DIM ] , const string pNames[ ] , const int pTurn )
{
  char mark; //the current player's mark                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              

  //make the mark based on the current player playing                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
  if( pTurn == 0 )
    mark = 'X';
  else //otherwise, pTurn == 1, so the second player is playing                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
    mark = 'O';

  //------------------------------- CHECKING FOR EACH COLUMN -------------------------------                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
  //check each column of the game table                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
  for( int c = 0 ; c < DIM ; c++ )
    {
      //checking for three of the same marks consecutively in the current column                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
      if( gTable[ c ][ 0 ] == mark && gTable[ c ][ 1 ] == mark && gTable[ c ][ 2 ] == mark )
        return true;
    }
  //--------------------------------- CHECKING FOR EACH ROW ---------------------------------                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
  //check each row of the game table                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
  for( int r = 0 ; r < DIM ; r++ )
    {
      //checking for three of the same marks consecutively in the current row                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
      if( gTable[ 0 ][ r ] == mark && gTable[ 1 ][ r ] == mark && gTable[ 2 ][ r ] == mark )
        return true;
    }
  //---------------------------------- CHECKING DIAGONALLY ----------------------------------                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
  //checking for diagonally from top to bottom for three of the same marks consecutively                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
  if( gTable[ 0 ][ 0 ] == mark && gTable[ 1 ][ 1 ] == mark && gTable[ 2 ][ 2 ] == mark )
    return true;
  //checking for diagonally from bottom to top for three of the same marks consecutively                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
  else if( gTable[ 0 ][ 2 ] == mark && gTable[ 1 ][ 1 ] == mark && gTable[ 2 ][ 0 ] == mark )
    return true;

  //if none of the winning situations were found, the game should continue                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
  return false;
}

/*******************************************************************************************************                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
This function will check if there is a tie by checking if there are any empty game table slots.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
gTable: the 2D array that contains the tic-tac-toe game table.                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
*******************************************************************************************************/
bool checkTie( const char gTable[ ][ DIM ] )
{
  //start checking from column to column ( vertically )                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
  for( int c = 0 ; c < DIM ; c++ )
    {
      //go through each row from the current column                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
      for( int r = 0 ; r < DIM ; r++ )
        {
          //if an empty space is found, there is no tie                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
          if( gTable[ c ][ r ] == ' ' )
            return false;
        }
    }

  //there is a tie if the game is still going and there is no empty spaces                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
  return true;
}
